import React, { useState, useEffect } from "react";
import { BASE_URL } from "../utils/config";
import "./bookings.css";

const Bookings = () => {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await fetch(`${BASE_URL}/booking`, {
          method: "GET",
          credentials: "include",
        });
        const data = await response.json();
        setBookings(data.data);
      } catch (error) {
        console.error("Error fetching bookings:", error);
      }
    };

    fetchBookings();
  }, []);

  const handleAccept = async (bookingId) => {
    try {
      const response = await fetch(`${BASE_URL}/booking/${bookingId}`, {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
        },
        credentials: "include",
        body: JSON.stringify({ status: "accepted" }),
      });

      if (response.ok) {
        // Update the local state with the modified booking
        setBookings((prevBookings) =>
          prevBookings.map((booking) =>
            booking._id === bookingId
              ? { ...booking, status: "accepted" }
              : booking
          )
        );
        console.log(`Booking with ID ${bookingId} accepted successfully.`);
      } else {
        console.error(`Error accepting booking with ID ${bookingId}.`);
      }
    } catch (error) {
      console.error("Error accepting booking:", error);
    }
  };

  const handleDelete = (bookingId) => {
    // Implement your delete logic here
    console.log(`Delete booking with ID: ${bookingId}`);
  };

  return (
    <div>
      <h2>All Bookings</h2>
      {bookings.length === 0 ? (
        <p>No bookings available.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Sl No</th>
              <th>Tour Name</th>
              <th>Guest Name</th>
              <th>No of People</th>
              <th>Booked At</th>
              <th>Manage</th>
            </tr>
          </thead>
          <tbody>
            {bookings.map((booking, index) => (
              <tr key={booking._id}>
                <td>{index + 1}</td>
                <td>{booking.tourName}</td>
                <td>{booking.fullName}</td>
                <td>{booking.guestSize}</td>
                <td>{booking.bookAt}</td>
                <td>
                  <button
                    className="accept"
                    onClick={() => handleAccept(booking._id)}
                  >
                    Accept
                  </button>
                  <button
                    className="delete"
                    onClick={() => handleDelete(booking._id)}
                  >
                    Decline
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Bookings;
