import React, { useState, useEffect } from "react";
import { BASE_URL } from "../utils/config";
import { useParams } from "react-router-dom";
import useFetch from "../hooks/useFetch";

const Userbookings = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const { userId } = useParams();

  useEffect(() => {
    const fetchUserBookings = async () => {
      try {
        const { data: bookings, loading } = useFetch(
          `${BASE_URL}/booking/${userId}`
        );
        const { fullName, guestSie, bookAt } = bookings;
        console.log("user id is ", userId);

        const data = await response.json();
        console.log(data);

        if (response.ok) {
          setBookings(data.data);
        } else {
          console.error("Error fetching user bookings:", data.message);
        }

        setLoading(false);
      } catch (error) {
        console.error("Error fetching user bookings:", error);
        setLoading(false);
      }
    };

    fetchUserBookings();
  }, [userId]);

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h2>User Bookings</h2>
      {bookings.length === 0 ? (
        <p>No bookings available for this user.</p>
      ) : (
        <ul>
          {bookings.map((booking) => (
            <li key={booking._id}>
              <p>
                <strong>Tour Name:</strong> {booking.tourName}
              </p>
              <p>
                <strong>Guest Name:</strong> {booking.fullName}
              </p>
              <p>
                <strong>Date:</strong> {booking.date}
              </p>
              <p>
                <strong>Booked At:</strong> {booking.bookAt}
              </p>
              {/* Add more details as needed */}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Userbookings;
