import React, { useState, useEffect } from "react";
import jwt from "jsonwebtoken";
import { BASE_URL } from "../utils/config";
import { useParams } from "react-router-dom";
import useFetch from "../hooks/useFetch";

const Userbookings = () => {
  const [bookings, setBookings] = useState([]);
  const { userId } = useParams();

  const getAccessToken = () => {
    const cookies = document.cookie.split("; ");
    const accessTokenCookie = cookies.find((row) =>
      row.startsWith("accessToken=")
    );

    if (accessTokenCookie) {
      return accessTokenCookie.split("=")[1];
    }

    return null;
  };

  // Function to decode JWT token
  const decodeToken = (token) => {
    try {
      const decoded = jwt.verify(token, process.env.JWT_SECRET_KEY);
      return decoded.id;
    } catch (error) {
      return null;
    }
  };

  // Get JWT token from cookies
  const accessToken = getAccessToken();
  const decodedUserId = accessToken ? decodeToken(accessToken) : null;

  // Set up headers with the decoded user ID
  const headers = {
    Authorization: `Bearer ${accessToken}`,
  };

  // Construct fetch URL based on the presence of userId
  const fetchUrl = userId
    ? `${BASE_URL}/booking/userbookings/${decodedUserId}`
    : `${BASE_URL}/booking/userbookings`;

  // UseFetch hook for fetching data
  const {
    data: fetchedBookings,
    loading,
    error,
  } = useFetch(fetchUrl, { headers });

  // Update state when data is fetched
  useEffect(() => {
    if (!loading && !error) {
      setBookings(fetchedBookings.data);
    }
  }, [fetchedBookings, loading, error]);

  // Loading state
  if (loading) {
    return <p>Loading...</p>;
  }

  // Error state
  if (error) {
    return <p>Error fetching user bookings: {error}</p>;
  }

  // Render the component
  return (
    <div>
      <h2>User Bookings</h2>
      {bookings.length === 0 ? (
        <p>No bookings available for this user.</p>
      ) : (
        <ul>
          {bookings.map((booking) => (
            <li key={booking._id}>
              <p>
                <strong>Tour Name:</strong> {booking.tourName}
              </p>
              <p>
                <strong>Guest Name:</strong> {booking.fullName}
              </p>
              <p>
                <strong>Date:</strong> {booking.date}
              </p>
              <p>
                <strong>Booked At:</strong> {booking.bookAt}
              </p>
              {/* Add more details as needed */}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Userbookings;
