import React, { useState, useEffect } from "react";
import { BASE_URL } from "../utils/config";
import Card from "../components/Card";

const AdminHome = () => {
  const [userCount, setUserCount] = useState(null);
  // const [bookingCount, setBookingCount] = useState(null);
  // const [tourCount, setTourCount] = useState(null);

  useEffect(() => {
    // Fetch counts from the backend API
    const fetchData = async () => {
      try {
        const userResponse = await fetch(`${BASE_URL}/users/countUser`);
        const userData = await userResponse.json();
        setUserCount(userData.data);
        console.log(userData);

        // const bookingResponse = await fetch('/api/bookings/countBookings');
        // const bookingData = await bookingResponse.json();
        // setBookingCount(bookingData.data);

        // const tourResponse = await fetch('/api/tours/countTours');
        // const tourData = await tourResponse.json();
        // setTourCount(tourData.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="admin-home">
      <h1>Welcome to Admin Dashboard</h1>

      <div className="card-container">
        <Card title="Users" count={userCount} />
        {/* <Card title="Bookings" count={bookingCount} />
        <Card title="Tours" count={tourCount} /> */}
      </div>
    </div>
  );
};

export default AdminHome;
