// ToursAdmin.jsx

import React, { useState, useEffect } from "react";
import { BASE_URL } from "../utils/config";
import "./ToursAdmin.css"; //
import { Button, Form, FormGroup, Label, Input } from "reactstrap"; // Import Form components from Reactstrap

const Toursadmin = () => {
  const [tours, setTours] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddTourForm, setShowAddTourForm] = useState(false); // New state for controlling the display of the form

  useEffect(() => {
    const fetchTours = async () => {
      try {
        const response = await fetch(`${BASE_URL}/tours?page=0`, {
          method: "GET",
          credentials: "include",
        });
        if (!response.ok) {
          throw new Error("Failed to fetch tours");
        }
        const data = await response.json();
        setTours(data.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching tours:", error);
        setLoading(false);
      }
    };

    fetchTours();
  }, []);

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`${BASE_URL}/tours/${id}`, {
        method: "DELETE",
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error("Failed to delete tour");
      }

      // Filter out the deleted tour from the state
      setTours((prevTours) => prevTours.filter((tour) => tour._id !== id));
    } catch (error) {
      console.error("Error deleting tour:", error);
    }
  };

  const handleAddTourClick = () => {
    setShowAddTourForm(true);
  };

  const handleAddTourSubmit = async (event) => {
    event.preventDefault();
    // Implement logic to handle form submission and add the new tour
    // You can use the form data in the state or use refs to get the values from form fields
    // Call your API to add the new tour and update the state with the new data
    // After successful addition, set setShowAddTourForm to false to hide the form
    setShowAddTourForm(false);
  };

  return (
    <div>
      <h1>Tours Admin</h1>
      <Button variant="contained" color="primary" onClick={handleAddTourClick}>
        Add tour
      </Button>
      {showAddTourForm && (
        <Form onSubmit={handleAddTourSubmit}>
          <FormGroup>
            <Label for="title">Title</Label>
            <Input
              type="text"
              name="title"
              id="title"
              placeholder="Enter title"
              required
            />
          </FormGroup>
          <FormGroup>
            <Label for="city">City</Label>
            <Input
              type="text"
              name="city"
              id="city"
              placeholder="Enter city"
              required
            />
          </FormGroup>
          <FormGroup>
            <Label for="address">Address</Label>
            <Input
              type="text"
              name="address"
              id="address"
              placeholder="Enter address"
              required
            />
          </FormGroup>
          <FormGroup>
            <Label for="distance">Distance</Label>
            <Input
              type="number"
              name="distance"
              id="distance"
              placeholder="Enter distance"
              required
            />
          </FormGroup>
          <FormGroup>
            <Label for="photo">Photo URL</Label>
            <Input
              type="text"
              name="photo"
              id="photo"
              placeholder="Enter photo URL"
              required
            />
          </FormGroup>
          <FormGroup>
            <Label for="desc">Description</Label>
            <Input
              type="textarea"
              name="desc"
              id="desc"
              placeholder="Enter description"
              required
            />
          </FormGroup>
          <FormGroup>
            <Label for="price">Price</Label>
            <Input
              type="number"
              name="price"
              id="price"
              placeholder="Enter price"
              required
            />
          </FormGroup>
          <FormGroup>
            <Label for="maxGroupSize">Max Group Size</Label>
            <Input
              type="number"
              name="maxGroupSize"
              id="maxGroupSize"
              placeholder="Enter max group size"
              required
            />
          </FormGroup>
          {/* Reviews field (assuming it's an array of MongoDB ObjectId references) */}
          <FormGroup>
            <Label for="reviews">Reviews</Label>
            <Input
              type="text"
              name="reviews"
              id="reviews"
              placeholder="Enter reviews"
            />
          </FormGroup>
          {/* Featured field (assuming it's a boolean) */}
          <FormGroup>
            <Label for="featured">Featured</Label>
            <CustomInput
              type="checkbox"
              id="featured"
              name="featured"
              label="Is Featured"
            />
          </FormGroup>
          <Button color="primary" type="submit">
            Save
          </Button>{" "}
          <Button color="secondary" onClick={() => setShowAddTourForm(false)}>
            Cancel
          </Button>
        </Form>
      )}
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="tour-card-container">
          {tours.map((tour) => (
            <div key={tour._id} className="tour-card">
              <img src={tour.photo} alt="tour-img" />
              <h2>{tour.title}</h2>
              <h4>Rs.{tour.price}</h4>
              <p>{tour.address}</p>
              <p>{tour.description}</p>
              <div className="button-container">
                <Button
                  color="danger"
                  variant="contained"
                  onClick={() => handleDelete(tour._id)}
                >
                  Delete
                </Button>{" "}
                <span className="spacer"></span>{" "}
                <Button color="primary" variant="contained">
                  Edit
                </Button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Toursadmin;
