import React, { useState, useEffect, useContext } from "react";
import { useParams } from "react-router-dom";
import { BASE_URL } from "../utils/config";
import Cookies from "js-cookie";
import { AuthContext } from ""; // Update the path

const UserBookings = () => {
  const { userId } = useParams();
  const { user } = useContext(AuthContext); // Access user information from the context

  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        // Retrieve accessToken from cookie
        const accessToken = Cookies.get("accessToken");

        if (!accessToken) {
          console.error("accessToken not found in cookies");
          return;
        }

        // Use userId from the context (AuthContext) instead of the URL params
        const response = await fetch(
          `${BASE_URL}/booking/userbookings/${user?.userId || userId}`, // Use user.userId from the context
          {
            headers: {
              Authorization: `Bearer ${accessToken}`,
            },
          }
        );

        if (!response.ok) {
          throw new Error(`Failed to fetch data: ${response.status}`);
        }

        const data = await response.json();
        setBookings(data.data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchBookings();
  }, [user, userId]);

  // Rest of the component code...
};
