// ToursAdmin.jsx

import React, { useState, useEffect } from "react";
import { BASE_URL } from "../utils/config";
import "./ToursAdmin.css"; //
import { Button, Form, FormGroup, Label, Input } from "reactstrap"; // Import Form components from Reactstrap

const Toursadmin = () => {
  const [tours, setTours] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddTourForm, setShowAddTourForm] = useState(false); // New state for controlling the display of the form

  useEffect(() => {
    const fetchTours = async () => {
      try {
        const response = await fetch(`${BASE_URL}/tours?page=0`, {
          method: "GET",
          credentials: "include",
        });
        if (!response.ok) {
          throw new Error("Failed to fetch tours");
        }
        const data = await response.json();
        setTours(data.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching tours:", error);
        setLoading(false);
      }
    };

    fetchTours();
  }, []);

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`${BASE_URL}/tours/${id}`, {
        method: "DELETE",
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error("Failed to delete tour");
      }

      // Filter out the deleted tour from the state
      setTours((prevTours) => prevTours.filter((tour) => tour._id !== id));
    } catch (error) {
      console.error("Error deleting tour:", error);
    }
  };

  const handleAddTourClick = () => {
    setShowAddTourForm(true);
  };

  return (
    <div>
      <h1>Tours Admin</h1>
      <Button variant="contained" color="primary" onClick={handleAddTourClick}>
        Add tour
      </Button>
      {showAddTourForm && (
        <Form>
          {/* Add your form input fields here */}
          <FormGroup>
            <Label for="title">Title</Label>
            <Input
              type="text"
              name="title"
              id="title"
              placeholder="Enter title"
            />
          </FormGroup>
          {/* Add other form fields as needed */}
          <Button color="primary" type="submit">
            Save
          </Button>{" "}
          <Button color="secondary" onClick={() => setShowAddTourForm(false)}>
            Cancel
          </Button>
        </Form>
      )}
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="tour-card-container">
          {tours.map((tour) => (
            <div key={tour._id} className="tour-card">
              {/* Existing code for displaying tour details */}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Toursadmin;
