import React, { useState, useEffect } from "react";
import { BASE_URL } from "../utils/config";
import { useParams } from "react-router-dom";
import useFetch from "../hooks/useFetch";

const Userbookings = () => {
  const [bookings, setBookings] = useState([]);
  const { userId } = useParams();

  const {
    data: fetchedBookings,
    loading,
    error,
  } = useFetch(`${BASE_URL}/booking/${userId}`);

  useEffect(() => {
    console.log("fetchedBookings:", fetchedBookings);
    console.log("loading:", loading);
    console.log("error:", error);
    if (!loading && !error) {
      setBookings(fetchedBookings.data);
    }
  }, [fetchedBookings, loading, error]);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error fetching user bookings: {error}</p>;
  }

  return (
    <div>
      <h2>User Bookings</h2>
      {bookings.length === 0 ? (
        <p>No bookings available for this user.</p>
      ) : (
        <ul>
          {bookings.map((booking) => (
            <li key={booking._id}>
              <p>
                <strong>Tour Name:</strong> {booking.tourName}
              </p>
              <p>
                <strong>Guest Name:</strong> {booking.fullName}
              </p>
              <p>
                <strong>Date:</strong> {booking.date}
              </p>
              <p>
                <strong>Booked At:</strong> {booking.bookAt}
              </p>
              {/* Add more details as needed */}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Userbookings;
