import React, { useState, useEffect } from "react";
import { BASE_URL } from "../utils/config";
import Card from "../components/Card";
import "../styles/adminhome.css";

const AdminHome = () => {
  const [userCount, setUserCount] = useState(0); // Default to 0
  const [tourCount, setTourCount] = useState(0); // Default to 0
  const [bookingCount, setBookingCount] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const userResponse = await fetch(`${BASE_URL}/users/countUser`, {
          method: "GET",
          credentials: "include",
        });

        if (userResponse.ok) {
          const userData = await userResponse.json();
          setUserCount(userData.data || 0); // Use data or default to 0 if undefined
        } else {
          console.error("Error fetching user count:", userResponse.status);
        }
      } catch (error) {
        console.error("Error fetching user data:", error);
      }

      try {
        const tourResponse = await fetch(
          `${BASE_URL}/tours/search/getTourCount`,
          {
            method: "GET",
            credentials: "include",
          }
        );

        if (tourResponse.ok) {
          const tourCountData = await tourResponse.json();
          setTourCount(tourCountData.data || 0); // Use data or default to 0 if undefined
        } else {
          console.error("Error fetching tour count:", tourResponse.status);
        }
      } catch (error) {
        console.error("Error fetching tour data:", error);
      }

      try {
        const bookingResponse = await fetch(
          `${BASE_URL}/bookings/countBookings`,
          {
            method: "GET",
            credentials: "include",
          }
        );

        if (bookingResponse.ok) {
          const bookingData = await bookingResponse.json();
          setBookingCount(bookingData.data || 0); // Use data or default to 0 if undefined
        } else {
          console.error(
            "Error fetching booking count:",
            bookingResponse.status
          );
        }
      } catch (error) {
        console.error("Error fetching booking data:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="admin-home">
      <h1>Welcome to Admin Dashboard</h1>
      <div className="card-container">
        <Card title="Users" count={userCount} />
        <Card title="Tours" count={tourCount} />
        <Card title="Bookings" count={bookingCount} />
      </div>
    </div>
  );
};

export default AdminHome;
